import { useState, useEffect } from 'react';
import axios from "axios";

export function useApplicationData(params) {
  const [state, setState] = useState({
    day: "Monday",
    days: [],
    appointments: []
  });

  const setDay = (day) => {
    setState(state => ({ ...state, day }));
  }

  function bookInterview(id, interview) {
    const appointment = {
      ...state.appointments[id],
      interview: { ...interview }
    };
    const appointments = {
      ...state.appointments,
      [id]: appointment
    };
    setState({
      ...state,
      appointments: appointments
    });
    if (interview) {
      return (axios
        .put(`http://localhost:8001/api/appointments/${id}`, {
        interview
      })
        .then((res) => {
          console.log(res);
          setState({
            ...state,
            appointments: appointments
          })
        }
        )

      )
    }
  }

  function cancelInterview(id) {

    setState({
      ...state,
      [id]: {
        ...state.appointments[id],
        interview: null
      }
    })
    if (id) {
      return axios
        .delete(`http://localhost:8001/api/appointments/${id}`)
        .then((res) => {
          console.log(res);
          setState({
            ...state,
            interview: null
          })
        })

      }
    }

    function getSpotsForDay (day, appointments) {
      let spots = 0;
      //  ITERATE  APPOINTMENT ID
      for (const id of day.appointments) {
        const appointment = appointments[id];
        if (!appointment.interview) {
          spots++;
        }
      }
      return spots;
    }

    function updateSpots(state, appointments, id) {
      // setState()
      const newDays = [...state.days]

      //GET DAY OBJECT. USE FIND. BRINGS BACK ONE VALUE
      const dayObj = newDays.find(day => day.name === state.day)

      const spots = getSpotsForDay(dayObj, appointments);

      // COUNT THE NULL = AVAILABLE SPOT

      // 5 - (TRUTHY APPOINTMENT IDS) = AVAILABLE SPOTS
      // CHANGES WHEN BOOKINTERVIEW/CANCEL INTERVIEW IS CALLED (SUCCESSFUL)
      console.log("spots === ", spots);
      const day = {...dayObj, spots}
      //return days array
      return newDays;
    }

    useEffect(() => {

      Promise.all([
        axios.get("/api/days"),
        axios.get("/api/appointments"),
        axios.get("/api/interviewers")
      ]).then((all) => {
        const [days, appointments, interviewers] = all;
        setState(prev => ({ ...prev, days: days.data, appointments: appointments.data, interviewers: interviewers.data }));


      })
    }, [state.day])

    return { state, setDay, bookInterview, cancelInterview }
}
