import React from "react";
import Header from "./Header";
import Show from "./Show";
import Empty from "./Empty";
import Form from "./Form";
import Status from "./Status";
import Confirm from "./Confirm";
import { useVisualMode } from "hooks/useVisualMode";
import 'components/Appointment/styles.scss'


export default function Appointment(props) {
const { time, interview, interviewers, bookInterview, id, cancelInterview } = props;
  const EMPTY = "EMPTY";
  const SHOW = "SHOW";
  const CREATE = "CREATE";
  const SAVING = "SAVING";
  const CONFIRM = "CONFIRM"
  const DELETING = "DELETING"

const { mode, transition, back } = useVisualMode(
  interview ? SHOW : EMPTY
)

function save(name, interviewer) {

    const interview = {
      student: name,
      interviewer: interviewer
    };
    transition(SAVING);
    bookInterview(id, interview)
    .then(() => {
      transition(SHOW);
    })

}

function handleCancelInterview(id) {
  props.cancelInterview(id)
}

  return (
    <article className="appointment">
      <Header time={time}/>
      {mode === EMPTY && <Empty onAdd={() => transition(CREATE)} />}
    {mode === SHOW && (
      <Show
        student={interview.student}
        interviewer={interview.interviewer}
        onDelete={handleCancelInterview}
        // onEdit={() => transition(EM√PTY)} // TODO: ADD  BACK LATER
      />
    )}
    {mode === CREATE && (
      <Form
        interviewers={interviewers}
        onCancel={() => back(EMPTY)}
        onSave={save}
      />
    )}
    {mode === SAVING && <Status message={SAVING} />}
    {mode === DELETING && (
      <Confirm 
        message={"Delete the appointment?"}
        onCancel={() => back(SHOW)}
        onConfirm={() => transition(EMPTY)}
      />
    )}
    </article>
  )
}
