import React, { useState, useEffect } from "react";
import axios from "axios";
import DayList from "./DayList";
import Appointment from "./Appointment";
import { getAppointmentsForDay, getInterview, getInterviewersForDay } from "helpers/selectors";

import "components/Application";
import "components/Application.scss";


export default function Application(props) {
  const [state, setState] = useState({
    day: "Monday",
    days: [],
    appointments: []
  });

  const { day, days } = state;
  const setDay = (day) => {
    setState(state => ({ ...state, day }));
  }
  const dailyAppointments = getAppointmentsForDay(state, day);
  const dailyInterviewers = getInterviewersForDay(state, day);

  function bookInterview(id, interview) {
    const appointment = {
      ...state.appointments[id],
      interview: { ...interview }
    };
    const appointments = {
      ...state.appointments,
      [id]: appointment
    };
    setState({
      ...state,
      appointments: appointments
    });
    if (interview) {
      return (axios
        .put(`http://localhost:8001/api/appointments/${id}`, {
        interview
      })
        .then((res) => {
          console.log(res);
          setState({
            ...state,
            appointments: appointments
          })
        }
        )
      )
    }
    console.log("this is interview at bookInterview", appointments);
  }

  function cancelInterview(id) {
    console.log('cancelling interview')

    // const appointments = {
    //   ...state.appointments,
    //   [id]: appointmentToDelete
    // }
    setState({
      ...state,
      [id]: {
        ...state.appointments[id],
        interview: null
      }
    })
    if (id) {
      return (axios
        .delete(`http://localhost:8001/api/appointments/${id}`)
        .then((res) => {
          console.log(res);
          setState({
            ...state,
            interview: null
          })
        })
      )
    }
  }

  useEffect(() => {

    Promise.all([
      axios.get("/api/days"),
      axios.get("/api/appointments"),
      axios.get("/api/interviewers")
    ]).then((all) => {
      const [days, appointments, interviewers] = all;
      setState(prev => ({ ...prev, days: days.data, appointments: appointments.data, interviewers: interviewers.data }));


    })
  }, [state.day])


  return (
    <main className="layout">
      <section className="sidebar">
        <img
          className="sidebar--centered"
          src="images/logo.png"
          alt="Interview Scheduler"
        />
        <hr className="sidebar__separator sidebar--centered" />
        <nav className="sidebar__menu">
          {
            <DayList
              days={days}
              day={day}
              setDay={setDay}
            />
          }
        </nav>
        <img
          className="sidebar__lhl sidebar--centered"
          src="images/lhl.png"
          alt="Lighthouse Labs"
        />
      </section>
      <section className="schedule">
        {
          dailyAppointments && dailyAppointments.map((appointment) => {
            const interview = getInterview(state, appointment.interview);

            return (
              <Appointment
                key={appointment.id}
                id={appointment.id}
                time={appointment.time}
                interview={interview}
                interviewers={dailyInterviewers}
                bookInterview={bookInterview}
                cancelInterview={cancelInterview}
              />
            )
          })
        }
      </section>
    </main>
  );
}
